import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js";

const TaskPage = ({ userName }) => {
  const [monthlyRevenue, setMonthlyRevenue] = useState(null);
  const [revenueByProduct, setRevenueByProduct] = useState(null);
  const [revenueByCustomer, setRevenueByCustomer] = useState(null);
  const [topCustomers, setTopCustomers] = useState(null);

  useEffect(() => {
    document.body.style.overflow = "hidden";
    return () => {
      document.body.style.overflow = "auto";
    };
  }, []);

  const fetchMonthlyRevenue = async () => {
    try {
      const response = await fetch("http://localhost:5002/api/monthly-revenue");
      const data = await response.json();
      setMonthlyRevenue(data);
      setRevenueByProduct(null);
      setRevenueByCustomer(null);
      setTopCustomers(null);
    } catch (error) {
      console.error("Error fetching monthly revenue:", error);
    }
  };

  const fetchRevenueByProduct = async () => {
    try {
      const response = await fetch(
        "http://localhost:5002/api/revenue-by-product"
      );
      const data = await response.json();
      setRevenueByProduct(data);
      setMonthlyRevenue(null);
      setRevenueByCustomer(null);
      setTopCustomers(null);
    } catch (error) {
      console.error("Error fetching revenue by product:", error);
    }
  };

  const fetchRevenueByCustomer = async () => {
    try {
      const response = await fetch(
        "http://localhost:5002/api/revenue-by-customer"
      );
      const data = await response.json();
      setRevenueByCustomer(data);
      setMonthlyRevenue(null);
      setRevenueByProduct(null);
      setTopCustomers(null);
    } catch (error) {
      console.error("Error fetching revenue by customer:", error);
    }
  };

  const fetchTopCustomers = async () => {
    try {
      const response = await fetch("http://localhost:5002/api/top-customers");
      const data = await response.json();
      setTopCustomers(data);
      setMonthlyRevenue(null);
      setRevenueByProduct(null);
      setRevenueByCustomer(null);
    } catch (error) {
      console.error("Error fetching top customers:", error);
    }
  };

  return (
    <div className="container-fluid">
      <header className="text-center my-4">
        <h2>Welcome, {userName}</h2>
        <div className="d-flex flex-wrap justify-content-center">
          <button
            onClick={fetchMonthlyRevenue}
            className="btn btn-primary my-2 mx-1 animate-button"
          >
            Compute Monthly Revenue
          </button>
          <button
            onClick={fetchRevenueByProduct}
            className="btn btn-secondary my-2 mx-1 animate-button"
          >
            Compute Revenue by Product
          </button>
          <button
            onClick={fetchRevenueByCustomer}
            className="btn btn-success my-2 mx-1 animate-button"
          >
            Compute Revenue by Customer
          </button>
          <button
            onClick={fetchTopCustomers}
            className="btn btn-danger my-2 mx-1 animate-button"
          >
            Identify Top 10 Customers
          </button>
        </div>
      </header>

      <div className="row">
        <div className="col-md-4 mb-4">
          <div className="accordion" id="welcomeAccordion">
            <div className="accordion-item">
              <h2 className="accordion-header" id="headingSummary">
                <button
                  className="accordion-button"
                  type="button"
                  data-bs-toggle="collapse"
                  data-bs-target="#collapseSummary"
                  aria-expanded="true"
                  aria-controls="collapseSummary"
                >
                  Summary
                </button>
              </h2>
              <div
                id="collapseSummary"
                className="accordion-collapse collapse show"
                aria-labelledby="headingSummary"
              >
                <div className="accordion-body">
                  This page provides insights into the revenue data, including
                  monthly revenue, revenue by product, and revenue by customer.
                  Use the buttons above to compute the desired metrics.
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-8">
          {monthlyRevenue && (
            <div className="mb-4" style={{ maxHeight: "400px", overflowY: "auto" }}>
              <h2 style={{ textAlign: "center" }}>Monthly Revenue</h2>
              <table className="table table-sm table-striped">
                <thead>
                  <tr>
                    <th style={{ textAlign: "center" }}>Month</th>
                    <th style={{ textAlign: "center" }}>Revenue</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(monthlyRevenue).map(([month, revenue]) => (
                    <tr key={month}>
                      <td style={{ textAlign: "center" }}>{month}</td>
                      <td style={{ textAlign: "center" }}>{revenue.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {revenueByProduct && (
            <div className="mb-4" style={{ maxHeight: "400px", overflowY: "auto" }}>
              <h2 style={{ textAlign: "center" }}>Revenue by Product</h2>
              <table className="table table-sm table-striped">
                <thead>
                  <tr>
                    <th style={{ textAlign: "center" }}>Product ID</th>
                    <th style={{ textAlign: "center" }}>Revenue</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(revenueByProduct).map(([productId, revenue]) => (
                    <tr key={productId}>
                      <td style={{ textAlign: "center" }}>{productId}</td>
                      <td style={{ textAlign: "center" }}>{revenue.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {revenueByCustomer && (
            <div className="mb-4" style={{ maxHeight: "400px", overflowY: "auto" }}>
              <h2 style={{ textAlign: "center" }}>Revenue by Customer</h2>
              <table className="table table-sm table-striped">
                <thead>
                  <tr>
                    <th style={{ textAlign: "center" }}>Customer ID</th>
                    <th style={{ textAlign: "center" }}>Revenue</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(revenueByCustomer).map(([customerId, revenue]) => (
                    <tr key={customerId}>
                      <td style={{ textAlign: "center" }}>{customerId}</td>
                      <td style={{ textAlign: "center" }}>{revenue.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {topCustomers && (
            <div className="mb-4" style={{ maxHeight: "400px", overflowY: "auto" }}>
              <h2 style={{ textAlign: "center" }}>Top 10 Customers</h2>
              <table className="table table-sm table-striped">
                <thead>
                  <tr>
                    <th style={{ textAlign: "center" }}>Customer ID</th>
                    <th style={{ textAlign: "center" }}>Revenue</th>
                  </tr>
                </thead>
                <tbody>
                  {topCustomers.map((customer, index) => (
                    <tr key={index}>
                      <td style={{ textAlign: "center" }}>{customer.customer_id}</td>
                      <td style={{ textAlign: "center" }}>{customer.revenue}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TaskPage;
